import { Component, Input, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ModalController } from '@ionic/angular';
import { AlertController } from '@ionic/angular';
import { UserService } from 'src/app/services/user.service';
import { Camera, CameraResultType } from '@capacitor/camera';
import { CameraSource } from '@capacitor/camera';

@Component({
  selector: 'app-business-owner-modal',
  templateUrl: './business-owner-modal.page.html',
  styleUrls: ['./business-owner-modal.page.scss'],
})
export class BusinessOwnerModalPage implements OnInit {
  @Input() email: string | null = null;
  @Input() monthly_fee_paid: boolean = false;

  businessOwnerForm!: FormGroup;

  constructor(
    private formBuilder: FormBuilder,
    private modalController: ModalController,
    private alertController: AlertController,
    private userService: UserService
  ) {}

  ngOnInit() {
    // Initialize the form
    this.businessOwnerForm = this.formBuilder.group({
      email: [{ value: this.email, disabled: true }],
      name: [''],
      image_owner: [''],
      adresse: [''],
      telephone1: [''],
      telephone2: [''],
      monthly_fee_paid: [{ value: this.monthly_fee_paid, disabled: true }],
    });
  }

  async onSubmit() {
    // Handle form submission here
    const formData = {
      ...this.businessOwnerForm.value, // Get values from form fields
      email: this.email, // Use the component input for email
      monthly_fee_paid: this.monthly_fee_paid, // Use the component input for monthly_fee_paid
    };

    // Check if the name field is null
    if (!formData.name) {
      console.error('Error: Name field cannot be null');
      await this.presentAlert('Error', 'Name field cannot be null');
      return; // Prevent form submission
    }
 
    try {
      // Upload the image
      const imageUrl = await this.userService.uploadImage(formData.image_owner);

      if (!imageUrl) {
        console.error('Error uploading image or generating URL');
        await this.presentAlert(
          'Error',
          'Failed to upload image or generate URL'
        );
        return;
      }

      // Update the form data with the image URL
      formData.image_owner = imageUrl;
      console.log('formData.image_owner', formData.image_owner);

      const addedOwner = await this.userService.addBusinessOwner(formData);
      console.log('addedOwner', addedOwner);

      if (addedOwner) {
        console.log('Business owner added successfully:', addedOwner);
        // Display an alert to notify the user
        this.presentAlert('Success', 'Business owner added successfully');
        await this.modalController.dismiss(addedOwner); // Pass addedOwner data
      } else {
        console.error('Failed to add business owner');
        await this.modalController.dismiss(addedOwner); // Pass addedOwner data
      }
    } catch (error) {
      console.error('Error adding business owner:', error);
      // Handle error as needed
    }
  }

  async presentAlert(header: string, message: string) {
    const alert = await this.alertController.create({
      header: header,
      message: message,
      buttons: ['OK'],
    });

    await alert.present();
  }

  onCancel() {
    // Dismiss the modal when cancel button is clicked
    this.modalController.dismiss();
  }

  addPhoto(event: any) {
    // Handle image upload logic here
    const file = event.target.files[0];
    console.log('Selected File:', file);
    this.businessOwnerForm.patchValue({
      image_owner: file, 
    });
  }

} 
<ion-header>
  <ion-toolbar>
    <ion-title> Business Owner Form </ion-title>
    <ion-buttons slot="start">
      <ion-button (click)="onCancel()" color="primary"> Cancel </ion-button>
    </ion-buttons>
    <ion-buttons slot="end">
      <ion-button (click)="onSubmit()" color="primary"> Done </ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>
  <form [formGroup]="businessOwnerForm">
    <ion-grid>
      <!-- Row 1 -->
      <ion-row>
        <!-- Column 1 -->
        <ion-col size="4"></ion-col>
        <!-- Column 2 -->
        <ion-col size="4" class="column-center">
          <div class="circle">
            <ion-icon
              name="person-circle-outline"
              class="contact-icon"
            ></ion-icon>
          </div>
        </ion-col>
        <!-- Column 3 -->
        <ion-col size="4"></ion-col>
      </ion-row>

      <!-- Row 2 -->
      <ion-row>
        <!-- Column 1 -->
        <ion-col size="4"></ion-col>
        <!-- Column 2 -->
        <ion-col size="4" class="column-center">
          <ion-button expand="block" (click)="fileInput.click()">Add Photo</ion-button>
            <input #fileInput type="file" accept="image/png, image/jpeg" (change)="addPhoto($event)" style="display:none" />
         
        </ion-col>
        <!-- Column 3 -->
        <ion-col size="4"></ion-col>
      </ion-row>

      <!-- Empty Row 1 -->
      <ion-row>
        <ion-col size="12">
          <!-- <ion-item lines="none">
            <ion-button expand="block" (click)="fileInput.click()">Add Photo</ion-button>
            <input #fileInput type="file" accept="image/png, image/jpeg" (change)="addPhoto($event)" style="display:none" />
          </ion-item> -->
        </ion-col>
      </ion-row>

      <!-- Empty Row 2 -->
      <ion-row>
        <ion-col size="12"></ion-col>
      </ion-row>

      <!-- Empty Row 1 -->
      <ion-row>
        <ion-col size="12"></ion-col>
      </ion-row>

      <!-- Empty Row 2 -->
      <ion-row>
        <ion-col size="12"></ion-col>
      </ion-row>

      <!-- Row 3 -->
      <ion-row>
        <ion-col size="12">
          <ion-item>
            <ion-label>Email :</ion-label>
            <ion-input
              formControlName="email"
              readonly="true"
              [value]="email"
            ></ion-input>
          </ion-item>
          <ion-item>
            <ion-label>Name :</ion-label>
            <ion-input formControlName="name"></ion-input>
          </ion-item>
          <ion-item>
            <ion-label>Adresse :</ion-label>
            <ion-input formControlName="adresse"></ion-input>
          </ion-item>
          <ion-item>
            <ion-label>Telephone 1 :</ion-label>
            <ion-input formControlName="telephone1"></ion-input>
          </ion-item>
          <ion-item>
            <ion-label>Telephone 2 :</ion-label>
            <ion-input formControlName="telephone2"></ion-input>
          </ion-item>
          <ion-item>
            <ion-label>Owner Image :</ion-label>
            <ion-input formControlName="image_owner" readonly></ion-input>
          </ion-item>
          <ion-item>
            <ion-label>Monthly Fee Paid :</ion-label>
            <ion-checkbox
              [checked]="monthly_fee_paid"
              disabled="true"
            ></ion-checkbox>
          </ion-item>
        </ion-col>
      </ion-row>
    </ion-grid>
  </form>
</ion-content>
